import React from 'react';
import {
    ColorValue,
    SafeAreaView,
    StyleSheet,
    View,
    FlatList,
    Image
} from 'react-native';
import Frame from './Frame';

interface ScrollingGridProps {
    size: number,
    data: any[],
    radius?: number,
    backgroundColor?: ColorValue
    gradient?: string, // for help with the gradients see: https://developer.mozilla.org/en-US/docs/Web/CSS/gradient/linear-gradient,
    children?: React.ReactNode,
    style?: {},
    numColumns: number
    gridPadding: number
};

const ScrollingFrame: React.FC<ScrollingGridProps> = ({ size, data, radius, backgroundColor, gradient, style, numColumns, gridPadding }) => {
    const childWidth = (size - (numColumns - 1) * gridPadding) / numColumns;
    const childHeight = childWidth;

    return (
        <Frame radius={radius} backgroundColor={backgroundColor} gradient={gradient} style={style}>
            <FlatList
                data={data}
                numColumns={numColumns}
                columnWrapperStyle={{
                    alignItems: 'center',
                    justifyContent: 'flex-start',

                }}
                keyExtractor={(item) => item.id}
                renderItem={({ item }) => {
                    return (
                        <View style={{ flex: 1, width: childWidth, height: childHeight, padding: gridPadding, margin: gridPadding }}>
                            {item}
                        </View>
                    );
                }}
            />
        </Frame>
    );
};

export default ScrollingFrame;
