import React from 'react';
import {
    ColorValue,
    SafeAreaView,
    StyleSheet,
    View,
    FlatList,
    Image
} from 'react-native';

interface ScrollingFrameProps {
    size: number,
    radius?: number,
    padding?: number,
    backgroundColor?: ColorValue,
    gradient?: string, // for help with the gradients see: https://developer.mozilla.org/en-US/docs/Web/CSS/gradient/linear-gradient
    image?: string,
    shadow?: number,
    children?: React.ReactNode,
    style?: {},
    numColumns: number
    gridPadding: number
};

const ScrollingFrame: React.FC<ScrollingFrameProps> = ({ size, radius, padding, backgroundColor, gradient, image, shadow, children, style, numColumns, gridPadding }) => {
    const childWidth = (size - (numColumns - 1) * gridPadding) / numColumns;
    const childHeight = childWidth;

    return (
        <View style={{
            ...style,
            height: size,
            width: size,
            backgroundColor: backgroundColor || 'rgb(255, 0, 0)',
            //@ts-ignore
            backgroundImage: image || gradient || null,
            borderRadius: radius || 8,
            padding: padding,
            boxShadow: '0 2px 4px rgba(0, 0, 0,' + shadow || 0 + ')',
            justifyContent: 'center',
            alignItems: 'center',
        }}>
            <FlatList
                data={data}
                numColumns={numColumns}
                columnWrapperStyle={{
                    flex: 1,
                    alignItems: 'center',
                    justifyContent: 'center',
                    margin: padding,
                }}
                keyExtractor={(item) => item.id}
                renderItem={({ item }) => {
                    return (
                        <View style={{ width: childWidth, height: childHeight }}>
                            {renderItem(item)}
                        </View>
                    );
                }}
            />
        </View>
    );
};

export default ScrollingFrame;
