import { useLinkProps } from '@react-navigation/native';
import React from 'react';
import { ColorValue, View, ScrollView } from 'react-native';

interface ScrollingFrameProps {
    size: number,
    radius?: number,
    padding?: number,
    backgroundColor?: ColorValue,
    gradient?: string, // for help with the gradients see: https://developer.mozilla.org/en-US/docs/Web/CSS/gradient/linear-gradient
    image?: string,
    shadow?: number,
    children?: React.ReactNode,
    style?: {},
    numColumns: number
    gridPadding: number
};

const ScrollingFrame: React.FC<ScrollingFrameProps> = ({ size, radius, padding, backgroundColor, gradient, image, shadow, children, style, numColumns, gridPadding }) => {
    const childWidth = (size - (numColumns - 1) * gridPadding) / numColumns;
    const childHeight = childWidth;
    const numRows = Math.ceil(React.Children.count(children) / numColumns);
    const gridHeight = numRows * childHeight + (numRows - 1) * gridPadding;

    return (
        <View style={{
            ...style,
            height: size,
            width: size,
            backgroundColor: backgroundColor || 'rgb(255, 0, 0)',
            //@ts-ignore
            backgroundImage: image || gradient || null,
            borderRadius: radius || 8,
            padding: padding,
            boxShadow: '0 2px 4px rgba(0, 0, 0,' + shadow || 0 + ')',
            alignItems: 'center',
        }}>
            <ScrollView>
                <View style={{ flexDirection: 'row', flexWrap: 'wrap', height: gridHeight, alignItems: 'center' }}>
                    {React.Children.map(children, (child, index) => {
                        return (
                            <View style={{ width: childWidth, height: childHeight, style: {} }}>
                                {child}
                            </View>
                        );
                    })}
                </View>
            </ScrollView>
        </View>
    );
};

export default ScrollingFrame;
