import { stringLiteral } from '@babel/types';
import React, { ReactElement } from 'react';
import { ColorValue, View } from 'react-native';

type FrameProps = {
    x: number,
    y: number,
    position: any,
    size: number,
    radius?: number,
    padding?: number,
    backgroundColor?: ColorValue,
    gradient?: string, // for help with the gradients see: https://developer.mozilla.org/en-US/docs/Web/CSS/gradient/linear-gradient
    image?: string,
    shadow?: number,
}

const SquareContainer = (props: FrameProps, children: ReactElement) => {
    return (
        <View style={{
            position: props.position || 'relative',
            top: props.y || 0,
            left: props.x || 0,
            width: props.size || 100,
            height: props.size || 100,
            backgroundColor: props.backgroundColor || 'rgb(255, 0, 0)',
            //@ts-ignore
            backgroundImage: props.image || props.gradient || null,
            borderRadius: props.radius || 8,
            paddingTop: props.padding || 5,
            paddingRight: props.padding || 5,
            paddingBottom: props.padding || 5,
            paddingLeft: props.padding || 5,
            boxShadow: '0 2px 4px rgba(0, 0, 0,' + props.shadow || 0 + ')',
        }}>
            {children}
        </View>
    );
};

export default SquareContainer;
