import React from 'react';
import {
    ColorValue,
    SafeAreaView,
    StyleSheet,
    View,
    FlatList,
    Image
} from 'react-native';
import Frame from './Frame';

interface ScrollingGridProps {
    size: number,
    data: any[],
    radius?: number,
    padding?: number,
    backgroundColor?: ColorValue,
    gradient?: string, // for help with the gradients see: https://developer.mozilla.org/en-US/docs/Web/CSS/gradient/linear-gradient
    image?: string,
    shadow?: number,
    children?: React.ReactNode,
    style?: {},
    numColumns: number
    gridPadding: number
};

const ScrollingFrame: React.FC<ScrollingGridProps> = ({ size, data, radius, padding, backgroundColor, gradient, image, shadow, style, numColumns, gridPadding }) => {
    const childWidth = (size - (numColumns - 1) * padding) / numColumns;
    const childHeight = childWidth;

    return (
        <Frame  >
            <FlatList
                data={data}
                numColumns={numColumns}
                columnWrapperStyle={{
                    alignItems: 'center',
                    justifyContent: 'center',

                }}
                keyExtractor={(item) => item.id}
                renderItem={({ item }) => {
                    return (
                        <View style={{ width: childWidth, height: childHeight, padding: padding, marginLeft: padding }}>
                            {item}
                        </View>
                    );
                }}
            />
        </Frame>
    );
};

export default ScrollingFrame;
